package com.syntax.javaclass07;

public class DoLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=10;
		while(num<=3) {
			System.out.println("Hello");
			num++;
		}
		System.out.println("--------");
		int num1=10;
		do {
			System.out.println("Hello");
			num1++;
		}while(num1<=3);
		
		int number=1;
		do {
			System.out.println(number+" ");
			number++;
		}while (number<=30);
		
		
		
		
		
		
		
		
		
		
		
	}

}
