package com.syntax.javaclass07;

public class LoopTask1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int d=2;
		while (d<=30) {
			System.out.println(d);
			d+=2;
			System.out.println("----");
		}
		int e=1;
		while (e<=30) {
			if (e % 2 ==0) {
				System.out.println(e+" ");
			}
			e++;
		}
		
		
		
	}

}
